package com.A03_Assignments3_Operators;

public class A06_TernaryOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 18;
		
		/// ternary means three blocks with a a condition, if true first block from : will print else the other block
		
	       String result = age < 100 ? "Less than 100" : "Greater than 100";
	       //can be used in where you can return to a variable
	       System.out.println(result); 

	}

}
