package com.LoopPracticeSBA;

public class S01_DisplayPatternA {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
System.out.println("Display Pattern A");
for(int i=1;i<=6;i++) {
	for(int j=1;j<=i;j++) {
		
		System.out.print(j+" ");
		
	}
	System.out.println();
}
	}

}
