package com.LoopPracticeSBA;

public class S01_MilestoKilometer {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
final double MILES_TO_KILOMETER=1.609;
System.out.println("Miles\t\t\tKilometers");
for(int m=1; m<=10;m++) {
	
	System.out.println(m+"\t\t\t"+MILES_TO_KILOMETER*m);
}
	}

}
