package com.ex_01_Basics2;

public class C03_Unary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int d=4;
		System.out.println(d++);
		
		d=4;
		System.out.println(++d);
		
		d=4;
		System.out.println(d--);
		
		d=4;
		System.out.println(--d);
		
		int result=10;
		result=result+1;
		System.out.println(result);
		
		
		result=10;
		result++;
		System.out.println(result);
		
		result=10;
		result+=1;
		System.out.println(result);

	}

}
